const primaryNav = document.getElementById("primary-navigation");
const navToggle = document.querySelector(".mobile-nav-toggle");
const menuContainer = document.querySelector(".hamburger-menu-container");
const myImage = new Image();
myImage.src =
  "data:image/jpeg;base64,";

const canvas = document.getElementById("pixel-rain-canvas");
const ctx = canvas.getContext("2d");

//nav Bar
navToggle.addEventListener("click", () => {
  const visibility = primaryNav.getAttribute("data-visible");

  if (visibility === "false") {
    primaryNav.setAttribute("data-visible", "true");
    navToggle.setAttribute("aria-expanded", "true");
  } else if (visibility === "true") {
    primaryNav.setAttribute("data-visible", "false");
    navToggle.setAttribute("aria-expanded", "false");
  }

  menuContainer.classList.toggle("change");
});

//Pixel Image Canvas

//Last step worked on was creating the set up function
function setUp() {
  canvas.width = document.querySelector(".top-content-item").clientWidth - 40;
  canvas.height = document.querySelector(".top-content-item").clientHeight - 10;
}

//might need to redraw and get image data again on resize
ctx.drawImage(myImage, 0, 0, canvas.width, canvas.height);
const pixels = ctx.getImageData(0, 0, canvas.width, canvas.height);
ctx.clearRect(0, 0, canvas.width, canvas.height);

let particlesArray = [];
const numberofParticles = 6000;

let mappedImage = [];

let started = false;
const setUpComplete = new CustomEvent("setUpComplete");
addEventListener("setUpComplete", init);

function setUpImage() {
  for (let y = 0; y < pixels.height; y++) {
    let row = [];
    for (let x = 0; x < pixels.width; x++) {
      const red = pixels.data[y * 4 * pixels.width + x * 4];
      const green = pixels.data[y * 4 * pixels.width + (x * 4 + 1)];
      const blue = pixels.data[y * 4 * pixels.width + (x * 4 + 2)];
      const brightness = calculateRelativeBrightness(red, green, blue);
      const cell = [
        (cellBrightness = brightness),
        (cellColor = "rgb(" + red + "," + green + "," + blue + ")"),
      ];
      row.push(cell);
    }
    mappedImage.push(row);
  }
  dispatchEvent(setUpComplete);
}

//console.log(mappedImage)

function calculateRelativeBrightness(red, green, blue) {
  return (
    Math.sqrt(red * red * 0.299 + green * green * 0.587 + blue * blue * 0.114) /
    100
  );
}

class Particle {
  constructor() {
    this.x = Math.random() * canvas.width;
    this.y = 0;
    this.speed = 0;
    this.velocity = Math.random() * 0.2 + 0.5;
    this.size = Math.random() * 1.5 + 1;
    this.position1 = Math.floor(this.y);
    this.position2 = Math.floor(this.x);
    this.angle = 0;
    this.move = true;
  }

  update() {
    this.position1 = Math.floor(this.y);
    this.position2 = Math.floor(this.x);
    if (
      mappedImage[this.position1] &&
      mappedImage[this.position1][this.position2]
    ) {
      this.speed = mappedImage[this.position1][this.position2][0];
    }
    let movement = 2.5 - this.speed + this.velocity;
    this.angle += this.speed * 0.08;

    this.y += movement; // movement + Math.sin(this.angle) * 3;
    //this.x += movement + Math.cos(this.angle) * 3;
    if (this.y >= canvas.height) {
      this.move = false;
      this.y = 0;
      this.x = Math.random() * canvas.width;
    }
    if (this.x >= canvas.width) {
      this.x = 0;
      this.y = Math.random() * canvas.height;
    }

    if (this.angle === 360) this.angle = 0;
  }

  draw() {
    ctx.beginPath();
    if (
      mappedImage[this.position1] &&
      mappedImage[this.position1][this.position2]
    ) {
      ctx.fillStyle = mappedImage[this.position1][this.position2][1];
    }
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

function init() {
  for (let i = 0; i < numberofParticles; i++) {
    particlesArray.push(new Particle());
  }
  animate();
}

setUpImage();

function animate() {
  //ctx.drawImage(myImage, 0, 0, canvas.width, canvas.height);
  ctx.globalAlpha = 0.05;
  ctx.fillStyle = "rgb(0, 0, 0)";
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.globalAlpha = 0.2;
  particlesArray.forEach((particle) => {
    if (particle.move) {
      particle.update();
      ctx.globalAlpha = particle.speed * 0.5;
      particle.draw();
    }
  });
  requestAnimationFrame(animate);
}

setInterval(reset, 12500);

function reset() {
  particlesArray.forEach((particle) => {
    if (particle.y !== 0) {
      particle.y = 0;
    }
    particle.move = true;
  });
}

addEventListener("resize", () => {});
